var elementVisible=-10,elements=[...document.querySelectorAll("[scroll]")];let moveFrom=["l","r","b","t"],moveto=["r","l","t","b"];window.addEventListener("scroll",()=>{elements.map(e=>{if(e.getBoundingClientRect().top<window.innerHeight-elementVisible)for(let t=0;t<=4;t++)e.getAttribute(`s-${moveFrom[t]}-to-${moveto[t]}`)&&e.setAttribute(`s-${moveFrom[t]}-to-${moveto[t]}`,"true")})});